mapper.select:
SELECT A.ID , (SELECT CODE_NM FROM common_code B WHERE A.REGION_CD = B.CODE_VAL) AS REGION_NM , (SELECT CODE_NM FROM common_code B WHERE A.AREA_CD = B.CODE_VAL) AS AREA_NM , A.ROOM_NM , A.THEME_NM , (SELECT CODE_NM FROM common_code B WHERE A.GENRE_CD = B.CODE_VAL) AS GENRE_NM , A.CONTENT , A.MIN_PARTY , A.MAX_PARTY , A.RUN_TIME , A.ACTIVITY , A.DEVICE_RATIO , A.PRICE , (SELECT COUNT(*) FROM tb_likes WHERE THEME_ID = A.ID) AS LIKES , A.RATING , A.LEVEL , A.IMG_PATH , A.CREATED_ID , A.CREATED_TIME , A.UPDATED_ID , A.UPDATED_TIME , A.NEW_THEME FROM ESCAPE_ROOM_LIST A WHERE 1 = 1
AND (REPLACE(A.ROOM_NM,' ','') LIKE CONCAT('%', #{keyword}, '%') OR REPLACE(A.THEME_NM,' ','') LIKE CONCAT('%', #{keyword}, '%') OR REPLACE(A.CONTENT,' ','') LIKE CONCAT('%', #{keyword}, '%'))
AND A.REGION_CD = #{region}
AND A.GENRE_CD = #{genre}

SELECT A.ID , (SELECT CODE_NM FROM common_code B WHERE A.REGION_CD = B.CODE_VAL) AS REGION_NM , (SELECT CODE_NM FROM common_code B WHERE A.AREA_CD = B.CODE_VAL) AS AREA_NM , A.ROOM_NM , A.THEME_NM , (SELECT CODE_NM FROM common_code B WHERE A.GENRE_CD = B.CODE_VAL) AS GENRE_NM , A.CONTENT , A.MIN_PARTY , A.MAX_PARTY , A.RUN_TIME , A.ACTIVITY , A.DEVICE_RATIO , A.PRICE , (SELECT COUNT(*) FROM tb_likes WHERE THEME_ID = A.ID) AS LIKES , A.RATING , A.LEVEL , A.IMG_PATH , A.CREATED_ID , A.CREATED_TIME , A.UPDATED_ID , A.UPDATED_TIME , A.NEW_THEME FROM ESCAPE_ROOM_LIST A LEFT OUTER JOIN tb_likes B ON A.ID = B.THEME_ID WHERE 1 = 1
AND (REPLACE(A.ROOM_NM,' ','') LIKE CONCAT('%', #{keyword}, '%') OR REPLACE(A.THEME_NM,' ','') LIKE CONCAT('%', #{keyword}, '%') OR REPLACE(A.CONTENT,' ','') LIKE CONCAT('%', #{keyword}, '%'))
AND A.REGION_CD = #{region}
AND A.GENRE_CD = #{genre}
AND B.USER_ID = #{userId} ORDER BY B.CREATED_TIME DESC

SELECT A.ID , (SELECT CODE_NM FROM common_code B WHERE A.REGION_CD = B.CODE_VAL) AS REGION_NM , (SELECT CODE_NM FROM common_code B WHERE A.AREA_CD = B.CODE_VAL) AS AREA_NM , A.ROOM_NM , A.THEME_NM , (SELECT CODE_NM FROM common_code B WHERE A.GENRE_CD = B.CODE_VAL) AS GENRE_NM , A.CONTENT , A.MIN_PARTY , A.MAX_PARTY , A.RUN_TIME , A.ACTIVITY , A.DEVICE_RATIO , A.PRICE , (SELECT COUNT(*) FROM tb_likes WHERE THEME_ID = A.ID) AS LIKES , A.RATING , A.LEVEL , A.IMG_PATH , A.CREATED_ID , A.CREATED_TIME , A.UPDATED_ID , A.UPDATED_TIME , A.NEW_THEME FROM ESCAPE_ROOM_LIST A LEFT OUTER JOIN tb_likes B ON A.ID = B.THEME_ID WHERE 1 = 1
AND (REPLACE(A.ROOM_NM,' ','') LIKE CONCAT('%', #{keyword}, '%') OR REPLACE(A.THEME_NM,' ','') LIKE CONCAT('%', #{keyword}, '%') OR REPLACE(A.CONTENT,' ','') LIKE CONCAT('%', #{keyword}, '%'))
AND A.REGION_CD = #{region}
AND A.GENRE_CD = #{genre}
AND B.USER_ID = #{userId} ORDER BY B.CREATED_TIME DESC

mapper.insert:
INSERT INTO ESCAPE_ROOM_LIST 
(ROOM_NM
, THEME_NM
, REGION_CD
, AREA_CD
, GENRE_CD
, CONTENT
, MIN_PARTY
, MAX_PARTY
, RUN_TIME
, ACTIVITY
, DEVICE_RATIO
, PRICE
, RATING
, LEVEL
, RCMD
, NEW_THEME
, IMG_PATH
, SITE
, CREATED_ID
, CREATED_TIME
, UPDATED_ID
, UPDATED_TIME
)
VALUES 
(#{portaNm}
, #{themeNm}
, #{regionCd}
, #{areaCd}
, #{genreCd}
, #{content}
, #{minParty}
, #{maxParty}
, #{runTime}
, #{activiti}
, #{diviceRatio}
, #{price}
, #{rating}
, #{level}
, #{rcmd}
, #{newTheme}
, #{imgPath}
, #{site}
, #{userId}
, NOW()
, #{userId}
, NOW())

mapper.update:
        UPDATE ESCAPE_ROOM_LIST
        SET THEME_NM        = #{map.themeNm}
            , ROOM_NM       = #{map.portaNm}
            , REGION_CD     = #{map.regionCd}
            , AREA_CD       = #{map.areaCd}
            , GENRE_CD      = #{map.genreCd}
			, CONTENT       = #{map.content}
            , MIN_PARTY     = #{map.minParty}
            , MAX_PARTY     = #{map.maxParty}
            , RUN_TIME      = #{map.runTime}
            , ACTIVITY      = #{map.activiti}
            , DEVICE_RATIO  = #{map.diviceRatio}
            , PRICE         = #{map.price}
            , RATING        = #{map.rating}
            , LEVEL         = #{map.level}
            , IMG_PATH      = #{map.imgPath}
            , SITE          = #{map.site}
            , UPDATED_ID    = #{map.userId}
            , UPDATED_TIME  = NOW()
			, NEW_THEME     = #{map.newTheme}
			, RCMD          = #{map.rcmd}
        WHERE ID = #{id}

UPDATE ESCAPE_ROOM_LIST
SET HIT = HIT + 1
WHERE ID = #{id}

mapper.delete:
DELETE FROM TB_LIKES
WHERE USER_ID = #{userId} 
AND THEME_ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM TB_LIKES
WHERE USER_ID = #{userId}
    AND THEME_ID = #{id}
